  "tables": [
    {
      "id": "users_table",
      "name": "users",
      "columns": [
        {
          "id": "user_id",
          "name": "id",
          "dataType": "INT",
          "nullable": false,
          "primaryKey": true,
          "autoIncrement": true
        },
        {
          "id": "user_email",
          "name": "email",
          "dataType": "VARCHAR",
          "length": 255,
          "nullable": false,
          "unique": true
        },
        {
          "id": "user_name",
          "name": "name",
          "dataType": "VARCHAR",
          "length": 100,
          "nullable": false
        },
        {
          "id": "user_created_at",
          "name": "created_at",
          "dataType": "TIMESTAMP",
          "nullable": false
        }
      ],
      "position": {"x": 100, "y": 100}
    },
    {
      "id": "posts_table",
      "name": "posts",
      "columns": [
        {
          "id": "post_id",
          "name": "id",
          "dataType": "INT",
          "nullable": false,
          "primaryKey": true,
          "autoIncrement": true
        },
        {
          "id": "post_user_id",
          "name": "user_id",
          "dataType": "INT",
          "nullable": false
        },
        {
          "id": "post_title",
          "name": "title",
          "dataType": "VARCHAR",
          "length": 255,
          "nullable": false
        },
        {
          "id": "post_content",
          "name": "content",
          "dataType": "TEXT",
          "nullable": true
        },
        {
          "id": "post_published",
          "name": "published",
          "dataType": "BOOLEAN",
          "nullable": false
        }
      ],
      "position": {"x": 400, "y": 100}
    }
  ],
  "foreignKeys": [
    {
      "id": "fk_posts_user",
      "fromTableId": "posts_table",
      "fromColumnId": "post_user_id",
      "toTableId": "users_table",
      "toColumnId": "user_id",
      "onDelete": "CASCADE",
      "onUpdate": "CASCADE"
    }
  ]
}

